exports.handler = async function(event, context) {
  try {
    const { message } = JSON.parse(event.body);
    

    // Información de diagnóstico
    let diagnosticInfo = {
        contentLoaded: Object.keys(courseContent).length > 0,
        contentSections: Object.keys(courseContent).length,
        filePath: path.join(__dirname, 'course-content.json'),
        fileExists: fs.existsSync(path.join(__dirname, 'course-content.json')),
        query: message
      };
      
      // Si no hay contenido cargado, devuelve solo la información de diagnóstico
      if (!diagnosticInfo.contentLoaded) {
        return {
          statusCode: 200,
          body: JSON.stringify({ 
            response: `Error: No se pudo cargar el contenido del curso. Información de diagnóstico: ${JSON.stringify(diagnosticInfo)}` 
          })
        };
      }

    // Búsqueda de secciones relevantes
    const relevantSections = findRelevantSections(message, courseContent);
    const contextText = relevantSections.join("\n\n----------\n\n");
    
    const response = await axios.post('https://api.groq.com/openai/v1/chat/completions', {
      model: "llama3-8b-8192",
      messages: [
        { 
          role: "system", 
          content: `Eres un asistente especializado en Billetaje y Reservas Aeronáuticas.
          
          Usa principalmente la siguiente información del curso para responder:
          
          ${contextText}
          
          Usa principalmente la siguiente información del curso para responder:
          
          ${contextText}
          
          Información administrativa del curso:
          - Profesor: Facundo Villafañe
          - Primer parcial: 30 de abril de 2025
          - Segundo parcial: 12 de junio de 2025 al 18 de junio 2025
          - Recuperatorio primer parcial: 19 de junio de 2025 al 25 de junio 2025
          - Recuperatorio segundo parcial: 19 de junio de 2025 al 25 de junio 2025
          - Horarios de clase: Miercoles de 18:00 a 20:00
          
          Para consultas sobre cambios de fechas, excepciones, o cualquier información administrativa que no esté específicamente mencionada aquí, indica al estudiante que debe consultar directamente con el Prof. Facundo Villafañe.
          
          Mantén tus respuestas claras y bien formateadas con listas, viñetas o párrafos cortos cuando sea apropiado.`
        },
        { role: "user", content: message }
      ],
      temperature: 0.3, // Temperatura más baja para respuestas más precisas
      max_tokens: 1024
    }, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${process.env.GROQ_API_KEY}`
      }
    });
    
    return {
      statusCode: 200,
      body: JSON.stringify({ response: response.data.choices[0].message.content })
    };
  } catch (error) {
    console.error('Error completo:', error);
    return {
      statusCode: 500,
      body: JSON.stringify({ 
        error: "Error al procesar la solicitud", 
        details: error.message 
      })
    };
  }
};

// Función para encontrar secciones relevantes
function findRelevantSections(query, content) {
    // Si no hay contenido, devolver un arreglo vacío
    if (!content || Object.keys(content).length === 0) {
      return [];
    }
  
    const queryWords = query.toLowerCase()
      .normalize("NFD").replace(/[\u0300-\u036f]/g, "") // Normalización para eliminar acentos
      .split(/\W+/)
      .filter(word => word.length > 3);
    
    // Calcula relevancia de cada sección
    const relevanceScores = Object.entries(content).map(([key, text]) => {
      const textLower = text.toLowerCase()
        .normalize("NFD").replace(/[\u0300-\u036f]/g, ""); // Normalización para eliminar acentos
      
      // Puntaje básico: coincidencia de palabras
      let score = queryWords.reduce((sum, word) => {
        // Contar cuántas veces aparece la palabra
        const regex = new RegExp(`\\b${word}\\b`, 'gi');
        const matches = textLower.match(regex);
        return sum + (matches ? matches.length : 0);
      }, 0);
      
      // Bonus para frases exactas
      const queryLower = query.toLowerCase();
      if (textLower.includes(queryLower)) {
        score += 5;
      }
      
      return { key, score, text };
    });
    
    // Ordenar por relevancia y tomar las 3 secciones más relevantes
    const topSections = relevanceScores
      .sort((a, b) => b.score - a.score)
      .slice(0, 3)
      .filter(item => item.score > 0)
      .map(item => item.text);
    
    // Si no hay coincidencias, devolver un mensaje informativo
    if (topSections.length === 0) {
      return ["No se encontró información específica sobre esta consulta en el material del curso. Por favor, reformula tu pregunta o consulta directamente con el profesor."];
    }
    
    return topSections;
}